var arrLang = {
    'en': {
        'home': 'Home',
        'about': 'About',
        'portfolio' : 'Portfolio',
        'strength' : 'Strength',
        'contact' : 'Contact',
    },
    'ru': {
        'home': 'Дом',
        'about': ' меня',
        'portfolio' : 'Портфолио',
        'strength' : 'Знания',
        'contact' : 'Контакт',
    }
}
$(function() {
        $('.translate').click(function() {
            var lang = $(this).attr('id');

            $('.lang').each(function(index,item) {
                $(this).text(arrLang[lang][$(this).attr('key')]);
            });
        });
    });